(ns HAMM) ; mmmmmm...Ham

(defn mutations [DNA1, DNA2]
    (if (and (not (empty? DNA1)) (not (empty? DNA2)))
        (+ (mutations (rest DNA1) (rest DNA2))
          (if (= (first DNA1) (first DNA2))
              0
                1))
        0)
  )

(mutations "GAGCCTACTAACGGGAT" "CATCGTAATGACGGCCT")

